query:
fragment AppFragment on App {
  id
  name
  created
  isActive
  type
  homepageUrl
  appUrl
  configurationUrl
  supportUrl
  version
  accessToken
  privateMetadata {
    key
    value
    __typename
  }
  metadata {
    key
    value
    __typename
  }
  tokens {
    authToken
    id
    name
    __typename
  }
  webhooks {
    ...WebhookFragment
    __typename
  }
  __typename
}

fragment WebhookFragment on Webhook {
  id
  name
  isActive
  app {
    id
    name
    __typename
  }
  __typename
}

fragment AppErrorFragment on AppError {
  field
  message
  code
  permissions
  __typename
}

mutation AppCreate($input: AppInput!) {
  appCreate(input: $input) {
    authToken
    app {
      ...AppFragment
      __typename
    }
    errors: appErrors {
      ...AppErrorFragment
      __typename
    }
    __typename
  }
}
