query:
fragment CategoryTranslationFragment on Category {
  id
  name
  descriptionJson
  seoDescription
  seoTitle
  translation(languageCode: $language) {
    id
    descriptionJson
    language {
      language
      __typename
    }
    name
    seoDescription
    seoTitle
    __typename
  }
  __typename
}

mutation UpdateCategoryTranslations($id: ID!, $input: TranslationInput!, $language: LanguageCodeEnum!) {
  categoryTranslate(id: $id, input: $input, languageCode: $language) {
    errors {
      field
      message
      __typename
    }
    category {
      ...CategoryTranslationFragment
      __typename
    }
    __typename
  }
}
