query Home {
  salesToday: ordersTotal(period: TODAY) {
    gross {
      amount
      currency
      __typename
    }
    __typename
  }
  ordersToday: orders(created: TODAY) {
    totalCount
    __typename
  }
  ordersToFulfill: orders(status: READY_TO_FULFILL) {
    totalCount
    __typename
  }
  ordersToCapture: orders(status: READY_TO_CAPTURE) {
    totalCount
    __typename
  }
  productsOutOfStock: products(stockAvailability: OUT_OF_STOCK) {
    totalCount
    __typename
  }
  productTopToday: reportProductSales(period: TODAY, first: 5) {
    edges {
      node {
        id
        revenue(period: TODAY) {
          gross {
            amount
            currency
            __typename
          }
          __typename
        }
        attributes {
          values {
            id
            name
            __typename
          }
          __typename
        }
        product {
          id
          name
          thumbnail {
            url
            __typename
          }
          __typename
        }
        quantityOrdered
        __typename
      }
      __typename
    }
    __typename
  }
  activities: homepageEvents(last: 10) {
    edges {
      node {
        amount
        composedId
        date
        email
        emailType
        id
        message
        orderNumber
        oversoldItems
        quantity
        type
        user {
          id
          email
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
