query:
fragment AttributeFragment on Attribute {
  id
  name
  slug
  visibleInStorefront
  filterableInDashboard
  filterableInStorefront
  __typename
}

fragment ProductTypeFragment on ProductType {
  id
  name
  hasVariants
  isShippingRequired
  taxType {
    description
    taxCode
    __typename
  }
  __typename
}

fragment MetadataItem on MetadataItem {
  key
  value
  __typename
}

fragment MetadataFragment on ObjectWithMetadata {
  metadata {
    ...MetadataItem
    __typename
  }
  privateMetadata {
    ...MetadataItem
    __typename
  }
  __typename
}

fragment ProductTypeDetailsFragment on ProductType {
  ...ProductTypeFragment
  ...MetadataFragment
  productAttributes {
    ...AttributeFragment
    __typename
  }
  variantAttributes {
    ...AttributeFragment
    __typename
  }
  weight {
    unit
    value
    __typename
  }
  __typename
}

mutation ProductTypeUpdate($id: ID!, $input: ProductTypeInput!) {
  productTypeUpdate(id: $id, input: $input) {
    errors {
      field
      message
      __typename
    }
    productType {
      ...ProductTypeDetailsFragment
      __typename
    }
    __typename
  }
}
