query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment TaxTypeFragment on TaxType {
  description
  taxCode
  __typename
}

query SearchProductTypes($after: String, $first: Int!, $query: String!) {
  search: productTypes(after: $after, first: $first, filter: {search: $query}) {
    edges {
      node {
        id
        name
        hasVariants
        productAttributes {
          id
          inputType
          slug
          name
          valueRequired
          values {
            id
            name
            slug
            __typename
          }
          __typename
        }
        taxType {
          ...TaxTypeFragment
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
