query:
fragment CategoryTranslationFragment on Category {
  id
  name
  descriptionJson
  seoDescription
  seoTitle
  translation(languageCode: $language) {
    id
    descriptionJson
    language {
      language
      __typename
    }
    name
    seoDescription
    seoTitle
    __typename
  }
  __typename
}

query CategoryTranslationDetails($id: ID!, $language: LanguageCodeEnum!) {
  category(id: $id) {
    ...CategoryTranslationFragment
    __typename
  }
}
