query:
fragment PageFragment on Page {
  id
  title
  slug
  isPublished
  __typename
}

query PageList($first: Int, $after: String, $last: Int, $before: String, $sort: PageSortingInput) {
  pages(before: $before, after: $after, first: $first, last: $last, sortBy: $sort) {
    edges {
      node {
        ...PageFragment
        __typename
      }
      __typename
    }
    pageInfo {
      hasPreviousPage
      hasNextPage
      startCursor
      endCursor
      __typename
    }
    __typename
  }
}
