query:
fragment CustomerFragment on User {
  id
  email
  firstName
  lastName
  __typename
}

fragment AddressFragment on Address {
  city
  cityArea
  companyName
  country {
    __typename
    code
    country
  }
  countryArea
  firstName
  id
  lastName
  phone
  postalCode
  streetAddress1
  streetAddress2
  __typename
}

fragment CustomerAddressesFragment on User {
  ...CustomerFragment
  addresses {
    ...AddressFragment
    __typename
  }
  defaultBillingAddress {
    id
    __typename
  }
  defaultShippingAddress {
    id
    __typename
  }
  __typename
}

query CustomerAddresses($id: ID!) {
  user(id: $id) {
    ...CustomerAddressesFragment
    __typename
  }
}
