query:
fragment OrderPrice on TaxedMoney {
  gross {
    amount
    currency
    __typename
  }
  net {
    amount
    currency
    __typename
  }
  __typename
}

fragment Address on Address {
  id
  firstName
  lastName
  companyName
  streetAddress1
  streetAddress2
  city
  postalCode
  country {
    code
    country
    __typename
  }
  countryArea
  phone
  isDefaultBillingAddress
  isDefaultShippingAddress
  __typename
}

fragment Price on TaxedMoney {
  gross {
    amount
    currency
    __typename
  }
  net {
    amount
    currency
    __typename
  }
  __typename
}

fragment ProductVariant on ProductVariant {
  id
  name
  sku
  quantityAvailable
  isAvailable
  pricing {
    onSale
    priceUndiscounted {
      ...Price
      __typename
    }
    price {
      ...Price
      __typename
    }
    __typename
  }
  attributes {
    attribute {
      id
      name
      __typename
    }
    values {
      id
      name
      value: name
      __typename
    }
    __typename
  }
  product {
    id
    name
    thumbnail {
      url
      alt
      __typename
    }
    thumbnail2x: thumbnail(size: 510) {
      url
      __typename
    }
    productType {
      id
      isShippingRequired
      __typename
    }
    __typename
  }
  __typename
}

fragment OrderDetail on Order {
  userEmail
  paymentStatus
  paymentStatusDisplay
  status
  statusDisplay
  id
  token
  number
  shippingAddress {
    ...Address
    __typename
  }
  lines {
    productName
    quantity
    variant {
      ...ProductVariant
      __typename
    }
    unitPrice {
      currency
      ...OrderPrice
      __typename
    }
    totalPrice {
      currency
      ...OrderPrice
      __typename
    }
    __typename
  }
  subtotal {
    ...OrderPrice
    __typename
  }
  total {
    ...OrderPrice
    __typename
  }
  shippingPrice {
    ...OrderPrice
    __typename
  }
  __typename
}

fragment InvoiceFragment on Invoice {
  id
  number
  createdAt
  url
  status
  __typename
}

query UserOrderByToken($token: UUID!) {
  orderByToken(token: $token) {
    ...OrderDetail
    invoices {
      ...InvoiceFragment
      __typename
    }
    __typename
  }
}
