query OrdersByUser($perPage: Int!, $after: String) {
  me {
    id
    orders(first: $perPage, after: $after) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          id
          token
          number
          statusDisplay
          created
          total {
            gross {
              amount
              currency
              __typename
            }
            net {
              amount
              currency
              __typename
            }
            __typename
          }
          lines {
            id
            variant {
              id
              product {
                name
                id
                __typename
              }
              __typename
            }
            thumbnail {
              alt
              url
              __typename
            }
            thumbnail2x: thumbnail(size: 510) {
              url
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
