query:
fragment CustomerFragment on User {
  id
  email
  firstName
  lastName
  __typename
}

fragment AddressFragment on Address {
  city
  cityArea
  companyName
  country {
    __typename
    code
    country
  }
  countryArea
  firstName
  id
  lastName
  phone
  postalCode
  streetAddress1
  streetAddress2
  __typename
}

fragment CustomerDetailsFragment on User {
  ...CustomerFragment
  dateJoined
  lastLogin
  defaultShippingAddress {
    ...AddressFragment
    __typename
  }
  defaultBillingAddress {
    ...AddressFragment
    __typename
  }
  note
  isActive
  __typename
}

query CustomerDetails($id: ID!) {
  user(id: $id) {
    ...CustomerDetailsFragment
    orders(last: 5) {
      edges {
        node {
          id
          created
          number
          paymentStatus
          total {
            gross {
              currency
              amount
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    lastPlacedOrder: orders(last: 1) {
      edges {
        node {
          id
          created
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
