query:
fragment PermissionGroupFragment on Group {
  id
  name
  userCanManage
  users {
    id
    firstName
    lastName
    __typename
  }
  __typename
}

fragment PermissionFragment on Permission {
  code
  name
  __typename
}

fragment StaffMemberFragment on User {
  id
  email
  firstName
  isActive
  lastName
  __typename
}

fragment PermissionGroupDetailsFragment on Group {
  ...PermissionGroupFragment
  permissions {
    ...PermissionFragment
    __typename
  }
  users {
    ...StaffMemberFragment
    avatar(size: 48) {
      url
      __typename
    }
    __typename
  }
  __typename
}

query PermissionGroupDetails($id: ID!, $userId: ID!) {
  permissionGroup(id: $id) {
    ...PermissionGroupDetailsFragment
    __typename
  }
  user(id: $userId) {
    editableGroups {
      id
      __typename
    }
    userPermissions {
      code
      sourcePermissionGroups(userId: $userId) {
        id
        __typename
      }
      __typename
    }
    __typename
  }
}
