query:
fragment DiscountErrorFragment on DiscountError {
  code
  field
  __typename
}

fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment SaleFragment on Sale {
  id
  name
  type
  startDate
  endDate
  value
  __typename
}

fragment SaleDetailsFragment on Sale {
  ...SaleFragment
  products(after: $after, before: $before, first: $first, last: $last) {
    edges {
      node {
        id
        name
        isPublished
        productType {
          id
          name
          __typename
        }
        thumbnail {
          url
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    totalCount
    __typename
  }
  categories(after: $after, before: $before, first: $first, last: $last) {
    edges {
      node {
        id
        name
        products {
          totalCount
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    totalCount
    __typename
  }
  collections(after: $after, before: $before, first: $first, last: $last) {
    edges {
      node {
        id
        name
        products {
          totalCount
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    totalCount
    __typename
  }
  __typename
}

mutation SaleCataloguesAdd($input: CatalogueInput!, $id: ID!, $after: String, $before: String, $first: Int, $last: Int) {
  saleCataloguesAdd(id: $id, input: $input) {
    errors: discountErrors {
      ...DiscountErrorFragment
      __typename
    }
    sale {
      ...SaleDetailsFragment
      __typename
    }
    __typename
  }
}
