query:
fragment CustomerFragment on User {
  id
  email
  firstName
  lastName
  __typename
}

query ListCustomers($after: String, $before: String, $first: Int, $last: Int, $filter: CustomerFilterInput, $sort: UserSortingInput) {
  customers(after: $after, before: $before, first: $first, last: $last, filter: $filter, sortBy: $sort) {
    edges {
      node {
        ...CustomerFragment
        orders {
          totalCount
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      hasPreviousPage
      startCursor
      __typename
    }
    __typename
  }
}
