query ShippingQuery($id: ID!) {
    shippingZone(id: $id) {
        name
        shippingMethods {
            price {
                amount
            }
            minimumOrderWeight {
                value
                unit
            }
            maximumOrderWeight {
                value
                unit
            }
        }
        priceRange {
            start {
                amount
            }
            stop {
                amount
            }
        }
    }
}
