mutation assign($productTypeId: ID!, $operations: [AttributeAssignInput]!) {
  attributeAssign(productTypeId: $productTypeId, operations: $operations) {
    productErrors {
      field
      code
      message
      attributes
    }
    productType {
      id
      productAttributes {
        id
      }
      variantAttributes {
        id
      }
    }
  }
}
