query ($id: ID, $slug: String){
    category(
        id: $id,
        slug: $slug,
    ) {
        id
        name
        ancestors(first: 20) {
            edges {
                node {
                    name
                }
            }
        }
        children(first: 20) {
            edges {
                node {
                    name
                }
            }
        }
        products(first: 10) {
            edges {
                node {
                    id
                }
            }
        }
    }
}
