query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

query GridAttributes($first: Int!, $after: String, $ids: [ID!]!) {
  availableInGrid: attributes(first: $first, after: $after, filter: {availableInGrid: true, isVariantOnly: false}) {
    edges {
      node {
        id
        name
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    totalCount
    __typename
  }
  grid: attributes(first: 25, filter: {ids: $ids}) {
    edges {
      node {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
}
