mutation UpdatePrivateMetadata($id: ID!, $input: [MetadataInput!]!) {
    updatePrivateMetadata(
        id: $id
        input: $input
    ) {
        metadataErrors{
            field
            code
        }
        item {
            privateMetadata{
                key
                value
            }
            ...on User{
                id
            }
        }
    }
}
