query:
query Collection($id: ID!) {
  collection(id: $id) {
    id
    slug
    name
    seoDescription
    seoTitle
    backgroundImage {
      url
      __typename
    }
    __typename
  }
  attributes(filter: {inCollection: $id, filterableInStorefront: true}, first: 100) {
    edges {
      node {
        id
        name
        slug
        values {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
