query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment ProductTranslationFragment on Product {
  id
  name
  descriptionJson
  seoDescription
  seoTitle
  translation(languageCode: $language) {
    id
    descriptionJson
    language {
      code
      language
      __typename
    }
    name
    seoDescription
    seoTitle
    __typename
  }
  __typename
}

query ProductTranslations($language: LanguageCodeEnum!, $first: Int, $after: String, $last: Int, $before: String, $filter: ProductFilterInput) {
  products(before: $before, after: $after, first: $first, last: $last, filter: $filter) {
    edges {
      node {
        ...ProductTranslationFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
