mutation AppCreate(
    $name: String, $is_active: Boolean $permissions: [PermissionEnum]){
    appCreate(input:
        {name: $name, isActive: $is_active, permissions: $permissions})
    {
        authToken
        app{
            permissions{
                code
                name
            }
            id
            isActive
            name
            tokens{
                authToken
            }
        }
        appErrors{
            field
            message
            code
            permissions
        }
    }
}
