fragment OrderEventFragment on OrderEvent {
  id
  amount
  date
  email
  emailType
  invoiceNumber
  message
  quantity
  type
  user {
    id
    email
    __typename
  }
  __typename
}

fragment OrderErrorFragment on OrderError {
  code
  field
  __typename
}

mutation OrderAddNote($order: ID!, $input: OrderAddNoteInput!) {
  orderAddNote(order: $order, input: $input) {
    errors: orderErrors {
      ...OrderErrorFragment
      __typename
    }
    order {
      id
      events {
        ...OrderEventFragment
        __typename
      }
      __typename
    }
    __typename
  }
}
