mutation DraftOrderLinesCreate($orderId: ID!, $variantId: ID!, $quantity: Int!) {
    draftOrderLinesCreate(id: $orderId,
            input: [{variantId: $variantId, quantity: $quantity}]) {

        errors {
            field
            message
        }
        orderLines {
            id
            quantity
            productSku
        }
        order {
            total {
                gross {
                    amount
                }
            }
        }
    }
}
