query getCheckout($token: UUID!) {
    checkout(token: $token) {
       token,
       totalPrice {
            currency
            gross {
                amount
            }
        }
        subtotalPrice {
            currency
            gross {
                amount
            }
        }
       lines {
            totalPrice {
                currency
                gross {
                    amount
                }
            }
       }
    }
}
