mutation createAttribute(
    $name: String!, $slug: String, $values: [AttributeValueCreateInput]
){
    attributeCreate(input: {name: $name, values: $values, slug: $slug}) {
        errors {
            field
            message
        }
        productErrors {
            field
            message
            code
        }
        attribute {
            name
            slug
            values {
                name
                slug
            }
            productTypes(first: 10) {
                edges {
                    node {
                        id
                    }
                }
            }
        }
    }
}
