query:
mutation AppRetryInstall(
    $id: ID!, $activate_after_installation: Boolean){
    appRetryInstall(id:$id, activateAfterInstallation:$activate_after_installation){
        appInstallation{
            id
            status
            appName
            manifestUrl
        }
        appErrors{
            field
            message
            code
            permissions
        }
    }
}
