mutation DraftOrderLineUpdate($lineId: ID!, $quantity: Int!) {
    draftOrderLineUpdate(id: $lineId, input: {quantity: $quantity}) {
        errors {
            field
            message
        }
        orderLine {
            id
            quantity
        }
        order {
            total {
                gross {
                    amount
                }
            }
        }
    }
}
