query:
query User($id: ID!) {
    user(id: $id) {
        email
        firstName
        lastName
        isStaff
        isActive
        addresses {
            id
            isDefaultShippingAddress
            isDefaultBillingAddress
        }
        orders {
            totalCount
        }
        dateJoined
        lastLogin
        defaultShippingAddress {
            firstName
            lastName
            companyName
            streetAddress1
            streetAddress2
            city
            cityArea
            postalCode
            countryArea
            phone
            country {
                code
            }
            isDefaultShippingAddress
            isDefaultBillingAddress
        }
        defaultBillingAddress {
            firstName
            lastName
            companyName
            streetAddress1
            streetAddress2
            city
            cityArea
            postalCode
            countryArea
            phone
            country {
                code
            }
            isDefaultShippingAddress
            isDefaultBillingAddress
        }
        avatar {
            url
        }
        permissions {
            code
        }
        userPermissions {
            code
            sourcePermissionGroups(userId: $id) {
                name
            }
        }
        permissionGroups {
            name
            permissions {
                code
            }
        }
        editableGroups {
            name
        }
    }
}
