fragment Price on TaxedMoney {
  gross {
    amount
    currency
  }
  net {
    amount
    currency
  }
}

    fragment ProductVariant on ProductVariant {
      id
      name
      pricing {
        onSale
        priceUndiscounted {
          ...Price
        }
        price {
          ...Price
        }
      }
      product {
        id
        name
        thumbnail {
          url
          alt
        }
        thumbnail2x: thumbnail(size: 510) {
          url
        }
      }
    }

    fragment CheckoutLine on CheckoutLine {
      id
      quantity
      totalPrice {
        ...Price
      }
      variant {
        stockQuantity
        ...ProductVariant
      }
      quantity
    }

        mutation updateCheckoutLine($checkoutId: ID!, $lines: [CheckoutLineInput]!){
          checkoutLinesUpdate(checkoutId: $checkoutId, lines: $lines) {
            checkout {
              id
              lines {
                ...CheckoutLine
              }
              totalPrice {
                ...Price
              }
              subtotalPrice {
                ...Price
              }
              isShippingRequired
            }
            errors {
              field
              message
            }
          }
        }
