mutation createProduct(
        $productType: ID!,
        $category: ID!,
        $name: String!,
        $sku: String,
        $basePrice: PositiveDecimal!)
    {
        productCreate(
            input: {
                category: $category,
                productType: $productType,
                name: $name,
                sku: $sku,
                basePrice: $basePrice,
                weight: {value: 11.11, unit: "kg"}
            })
        {
            product {
                id
                weight{
                    value
                    unit
                }
            }
            productErrors {
                message
                field
                code
            }
        }
    }
