mutation SaleCreate(
        $name: String,
        $type: DiscountValueTypeEnum,
        $value: PositiveDecimal,
        $products: [ID]
) {
    saleCreate(input: {
            name: $name,
            type: $type,
            value: $value,
            products: $products
    }) {
        sale {
            id
        }
        errors {
            field
            message
        }
    }
}
