mutation checkoutShippingAddressUpdate(
        $checkoutId: ID!, $shippingAddress: AddressInput!) {
    checkoutShippingAddressUpdate(
            checkoutId: $checkoutId, shippingAddress: $shippingAddress) {
        checkout {
            token,
            id
        },
        errors {
            field,
            message,
        }
        checkoutErrors {
            field
            message
            code
        }
    }
}
