query:
mutation createProductType(
    $name: String,
    $slug: String,
    $taxCode: String,
    $hasVariants: Boolean,
    $isShippingRequired: Boolean,
    $productAttributes: [ID],
    $variantAttributes: [ID],
    $weight: WeightScalar) {
    productTypeCreate(
        input: {
            name: $name,
            slug: $slug,
            taxCode: $taxCode,
            hasVariants: $hasVariants,
            isShippingRequired: $isShippingRequired,
            productAttributes: $productAttributes,
            variantAttributes: $variantAttributes,
            weight: $weight}) {
        productType {
            name
            slug
            taxRate
            isShippingRequired
            hasVariants
            variantAttributes {
                name
                values {
                    name
                }
            }
            productAttributes {
                name
                values {
                    name
                }
            }
        }
        productErrors {
            field
            message
            code
        }
    }

}
