mutation checkoutLinesUpdate(
        $checkoutId: ID!, $lines: [CheckoutLineInput!]!) {
    checkoutLinesUpdate(checkoutId: $checkoutId, lines: $lines) {
        checkout {
            token
            lines {
                quantity
                variant {
                    id
                }
            }
        }
        checkoutErrors {
            field
            code
            message
        }
    }
}
