query:
fragment BasicProductFields on Product {
  id
  name
  thumbnail {
    url
    alt
  }
  thumbnail2x: thumbnail(size: 510) {
    url
  }
}

fragment Price on TaxedMoney {
  gross {
    amount
    currency
  }
  net {
    amount
    currency
  }
}

fragment ProductPricingField on Product {
  pricing {
    onSale
    priceRangeUndiscounted {
      start {
        ...Price
      }
      stop {
        ...Price
      }
    }
    priceRange {
      start {
        ...Price
      }
      stop {
        ...Price
      }
    }
  }
}

query FeaturedProducts {
  shop {
    homepageCollection {
      id
      products(first: 20) {
        edges {
          node {
            ...BasicProductFields
            ...ProductPricingField
            category {
              id
              name
            }
          }
        }
      }
    }
  }
}
