mutation ProductVariantStocksUpdate($variantId: ID!, $stocks: [StockInput!]!){
    productVariantStocksUpdate(variantId: $variantId, stocks: $stocks){
        productVariant{
            stocks{
                quantity
                quantityAllocated
                id
                warehouse{
                    slug
                }
            }
        }
        bulkStockErrors{
            code
            field
            message
            index
        }
    }
}
