mutation updateVariant (
    $id: ID!,
    $price: PositiveDecimal,
    $weight: WeightScalar
) {
    productVariantUpdate(
        id: $id,
        input: {
            weight: $weight,
            price: $price
        }
    ){
        productVariant {
            name
        }
        productErrors {
            field
            message
            code
        }
    }
}
