mutation updatePage($id: ID!, $slug: String,  $is_published: Boolean!) {
    pageUpdate(id: $id, input: {slug: $slug, isPublished: $is_published}) {
        page {
            id
            title
            slug
            isPublished
            publicationDate
        }
        pageErrors {
            field
            code
            message
        }
    }
}
