mutation createProduct($productTypeId: ID!, $collectionId: ID!, $categoryId: ID!) {
    productCreate(input: {
            name: "Product",
            basePrice: "2.5",
            productType: $productTypeId,
            isPublished: true,
            collections: [$collectionId],
            category: $categoryId
        }) {
        product {
            id,
            collections {
                slug
            },
            category {
                slug
            }
        }
        errors {
            message
            field
        }
    }
}
