query:
mutation CreatePage(
        $slug: String, $title: String, $content: String,
        $contentJson: JSONString, $isPublished: Boolean) {
    pageCreate(
            input: {
                slug: $slug, title: $title,
                content: $content, contentJson: $contentJson
                isPublished: $isPublished}) {
        page {
            id
            title
            content
            contentJson
            slug
            isPublished
            publicationDate
        }
        pageErrors {
            field
            code
            message
        }
    }
}
