query:
mutation collectionTranslate($collectionId: ID!) {
    collectionTranslate(
            id: $collectionId, languageCode: PL,
            input: {name: "Kolekcja PL"}) {
        collection {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
