query:
fragment AccountError on AccountError {
  code
  field
  message
  __typename
}

mutation TokenAuth($email: String!, $password: String!) {
  tokenCreate(email: $email, password: $password) {
    csrfToken
    refreshToken
    token
    errors: accountErrors {
      ...AccountError
      __typename
    }
    user {
      id
      __typename
    }
    __typename
  }
}
