query:
query ($filter: AppFilterInput ){
    apps(first: 5, filter: $filter){
        edges{
            node{
                id
                isActive
                permissions{
                    name
                    code
                }
                tokens{
                    authToken
                }
                webhooks{
                    name
                }
                name
                type
                aboutApp
                dataPrivacy
                dataPrivacyUrl
                homepageUrl
                supportUrl
                configurationUrl
                appUrl
            }
        }
    }
}
