mutation shippingPriceTranslate($shippingMethodId: ID!) {
    shippingPriceTranslate(
            id: $shippingMethodId, languageCode: PL,
            input: {name: "DHL PL"}) {
        shippingMethod {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
