mutation StaffNotificationRecipient (
    $id: ID!,
    $input: StaffNotificationRecipientInput!
) {
    staffNotificationRecipientUpdate(id: $id, input: $input) {
        staffNotificationRecipient {
            active
            email
            user {
                firstName
                lastName
                email
            }
        }
        shopErrors {
            field
            message
            code
        }
    }
}
