query ProductVariantDetails($id: ID!, $countyCode: CountryCode) {
    productVariant(id: $id) {
        id
        stocks(countryCode: $countyCode) {
            id
        }
        attributes {
            attribute {
                id
                name
                slug
                values {
                    id
                    name
                    slug
                }
            }
            values {
                id
                name
                slug
            }
        }
        costPrice {
            currency
            amount
        }
        images {
            id
        }
        name
        price {
            currency
            amount
        }
        product {
            id
        }
        weight {
            unit
            value
        }
    }
}
