fragment Price on TaxedMoney {
  gross {
    amount
    currency
    __typename
  }
  net {
    amount
    currency
    __typename
  }
  __typename
}

fragment ProductPricingField on Product {
  pricing {
    onSale
    priceRangeUndiscounted {
      start {
        ...Price
        __typename
      }
      stop {
        ...Price
        __typename
      }
      __typename
    }
    priceRange {
      start {
        ...Price
        __typename
      }
      stop {
        ...Price
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

query SearchProducts($query: String!, $attributes: [AttributeInput], $pageSize: Int, $sortBy: ProductOrder, $after: String) {
  products(filter: {search: $query, attributes: $attributes}, first: $pageSize, sortBy: $sortBy, after: $after) {
    totalCount
    edges {
      node {
        ...ProductPricingField
        id
        name
        thumbnail {
          url
          alt
          __typename
        }
        thumbnail2x: thumbnail(size: 510) {
          url
          __typename
        }
        category {
          id
          name
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      __typename
    }
    __typename
  }
  attributes(filter: {filterableInStorefront: true}, first: 100) {
    edges {
      node {
        id
        name
        slug
        values {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
