query:
fragment Price on TaxedMoney {
  gross {
    amount
    currency
  }
  net {
    amount
    currency
  }
}

    fragment ProductVariant on ProductVariant {
      id
      name
      pricing {
        onSale
        priceUndiscounted {
          ...Price
        }
        price {
          ...Price
        }
      }
      product {
        id
        name
        thumbnail {
          url
          alt
        }
        thumbnail2x: thumbnail(size: 510) {
          url
        }
      }
    }

    fragment CheckoutLine on CheckoutLine {
      id
      quantity
      totalPrice {
        ...Price
      }
      variant {
        stockQuantity
        ...ProductVariant
      }
      quantity
    }

fragment Address on Address {
  id
  firstName
  lastName
  companyName
  streetAddress1
  streetAddress2
  city
  postalCode
  country {
    code
    country
  }
  countryArea
  phone
  isDefaultBillingAddress
  isDefaultShippingAddress
}

fragment ShippingMethod on ShippingMethod {
  id
  name
  price {
    currency
    amount
  }
}

    fragment Checkout on Checkout {
      availablePaymentGateways {
        id
        name
        config {
          field
          value
        }
      }
      token
      id
      totalPrice {
        ...Price
      }
      subtotalPrice {
        ...Price
      }
      billingAddress {
        ...Address
      }
      shippingAddress {
        ...Address
      }
      email
      availableShippingMethods {
        ...ShippingMethod
      }
      shippingMethod {
        ...ShippingMethod
      }
      shippingPrice {
        ...Price
      }
      lines {
        ...CheckoutLine
      }
      isShippingRequired
      discount {
        currency
        amount
      }
      discountName
      translatedDiscountName
      voucherCode
    }

        mutation AddCheckoutPromoCode($checkoutId: ID!, $promoCode: String!) {
          checkoutAddPromoCode(checkoutId: $checkoutId, promoCode: $promoCode) {
            checkout {
              ...Checkout
            }
            errors {
              field
              message
            }
            checkoutErrors {
              field
              message
              code
            }
          }
        }
