query:
mutation voucherTranslate($voucherId: ID!) {
    voucherTranslate(
            id: $voucherId, languageCode: PL,
            input: {name: "Bon PL"}) {
        voucher {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
